package com.company.dao.pojo;

import java.io.Serializable;
import java.util.Date;

public class Check implements Serializable{

	/**
	 * 
	 */
	private static final long serialVersionUID = 1L;
	private int checkId;
	private int empno;
	private Date checkUp;
	private Date checkDown;
	
	
	public Check() {
		// TODO Auto-generated constructor stub
	}


	public Check(int checkId, int empno, Date checkUp, Date checkDown) {
		super();
		this.checkId = checkId;
		this.empno = empno;
		this.checkUp = checkUp;
		this.checkDown = checkDown;
	}


	public int getCheckId() {
		return checkId;
	}


	public void setCheckId(int checkId) {
		this.checkId = checkId;
	}


	public int getEmpno() {
		return empno;
	}


	public void setEmpno(int empno) {
		this.empno = empno;
	}


	public Date getCheckUp() {
		return checkUp;
	}


	public void setCheckUp(Date checkUp) {
		this.checkUp = checkUp;
	}


	public Date getCheckDown() {
		return checkDown;
	}


	public void setCheckDown(Date checkDown) {
		this.checkDown = checkDown;
	}


	@Override
	public String toString() {
		return "Check [checkId=" + checkId + ", empno=" + empno + ", checkUp=" + checkUp + ", checkDown=" + checkDown
				+ "]";
	}

	
	

}
